@import '../assets/css/vars.scss';

.header {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    min-height: 50px;
    border-bottom: 1px solid var(--color-main-bg);
    z-index: 10;
    transition: background 0.3s ease;

    @media (--viewport-tablet) {
        font-size: 0.75em;
    }

    &__wrap {
        max-width: var(--max-width);
        height: 6.8em;
        display: flex;
        justify-content: space-between;
        align-items: center;
        margin: 0 auto;
        padding: 1em 3em;

        @media (--viewport-tablet) {
            padding: 1em;
        }
    }

    &__nav {
        display: flex;
        height: 100%;
    }

    &__item {
        position: relative;
        height: 100%;
        padding: 0 1em;
        display: flex;
        align-items: center;

        @media (--viewport-tablet) {
            padding: 0 0.8em;
        }

        button {
            height: 100%;
            color: var(--color-accent-text);
            text-transform: uppercase;
            font-weight: 700;
        }

        &:before {
            content: '';
            position: absolute;
            top: 2.3em;
            left: -0.3em;
            display: block;
            width: 1em;
            height: 2px;
            background-color: var(--color-theme);
            opacity: 0;
            transition: opacity 0.3s ease;
        }

        &:hover {
            &:before {
                opacity: 1;
            }
        }
    }

    [data-state='scrolling'] & {
        background-color: var(--color-main-bg);
        border-color: var(--color-main-text);

        button {
            color: var(--color-main-text);
        }

        svg {
            path:first-child {
                fill: var(--color-main-text);
            }
        }
    }

    &__logo {
        @media (--viewport-tablet) {
            display: none;
        }
    }
}
